pub fn map(
  self: Pairs<key, value>,
  with: fn(key, value) -> result,
) -> Pairs<key, result> {
  when self is {
    [] -> []
    [Pair(k, v), ..rest] -> [Pair(k, with(k, v)), ..map(rest, with)]
  }
}

