modules.content:
//// This module implements arithmetic operations in a constrained 224-bit integer field.
//// Operations are performed modulo $2^{224}$, providing a field for cryptographic operations
//// that require 28-byte values.
////
//// The module provides functionality for basic arithmetic operations (addition, subtraction,
//// multiplication) within this constrained field, as well as conversion functions between
//// different representations.

use aiken/builtin
use aiken/crypto/bitwise

pub opaque type Bits224 {
  Bits224
}

pub type State =
  bitwise.State<Bits224>

type Over<a> =
  fn(State, a) -> State

/// The prime defining the 224-bit integer field $2^{224}$
pub const field =
  builtin.replicate_byte(28, 0)
    |> builtin.cons_bytearray(1, _)
    |> builtin.bytearray_to_integer(True, _)

/// The field size, in **bytes**.
pub const field_size = 28

// ## Constructing

/// Constructs a new `Bits224` element from a Big-Endian (most-significant bits first) `ByteArray`.
pub fn from_bytearray_big_endian(bytes: ByteArray) -> State {
  bytes
    |> builtin.bytearray_to_integer(True, _)
    |> bitwise.from_int(field)
}

/// Constructs a new `Bits224` element from a Little-Endian (least-significant bits first) `ByteArray`.
pub fn from_bytearray_little_endian(bytes: ByteArray) -> State {
  bytes
    |> builtin.bytearray_to_integer(False, _)
    |> bitwise.from_int(field)
}

/// Constructs a new `Bits224` element from an integer, ensuring it's within the valid range of the field.
pub fn from_int(int: Int) -> State {
  bitwise.from_int(int, field)
}

// ## Modifying

/// Exponentiates a `Bits224` element by a non-negative integer exponent, using repeated squaring.
/// Note that this function returns `zero` for negative exponents.
pub fn scale(self: State, e: Int) -> State {
  bitwise.scale(self, e, mul)
}

/// A faster version of `scale` for the case where the exponent is a power of two.
/// That is, the exponent $e = 2^k$ for some non-negative integer $k$.
pub fn scale2(self: State, k: Int) -> State {
  bitwise.scale2(self, k, mul)
}

// ## Combining

const add_s_bits224: Over<State> = bitwise.add_state(field)

/// Adds two `Bits224` elements, ensuring the result stays within the finite field range.
pub fn add(left: State, right: State) -> State {
  add_s_bits224(left, right)
}

const add_bit224: Over<ByteArray> = bitwise.add_bits(field, True)

/// Adds a ByteArray to a `Bits224` element, interpreting bytes as a big-endian number.
pub fn add_bytes(self: State, bytes: ByteArray) -> State {
  add_bit224(self, bytes)
}

const add_i224: Over<Int> = bitwise.add_int(field)

/// Adds an integer to a `Bits224` element.
pub fn add_int(self: State, int: Int) -> State {
  add_i224(self, int)
}

const mul_s_bits224: Over<State> = bitwise.mul_state(field)

/// Multiplies two `Bits224` elements, with the result constrained within the finite field.
pub fn mul(left: State, right: State) -> State {
  mul_s_bits224(left, right)
}

const mul_bit224: Over<ByteArray> = bitwise.mul_bits(field, True)

/// Multiplies a `Bits224` element by a ByteArray, interpreting bytes as a big-endian number.
pub fn mul_bytes(self: State, bytes: ByteArray) -> State {
  mul_bit224(self, bytes)
}

const mul_i224: Over<Int> = bitwise.mul_int(field)

/// Multiplies a `Bits224` element by an integer.
pub fn mul_int(self: State, int: Int) -> State {
  mul_i224(self, int)
}

const neg224: fn(State) -> State = bitwise.neg(field)

/// Calculates the additive inverse of a `Bits224` element.
pub fn neg(self: State) -> State {
  neg224(self)
}

const sub_s_bits224: Over<State> = bitwise.sub_state(field)

/// Subtracts one `Bits224` element from another, with the result wrapped within the finite field range.
pub fn sub(left: State, right: State) -> State {
  sub_s_bits224(left, right)
}

const sub_bit224: Over<ByteArray> = bitwise.sub_bits(field, True)

/// Subtracts a `ByteArray` from a `Bits224` element, interpreting bytes as a big-endian number.
pub fn sub_bytes(self: State, bytes: ByteArray) -> State {
  sub_bit224(self, bytes)
}

const sub_i224: Over<Int> = bitwise.sub_int(field)

/// Subtracts an integer from a `Bits224` element.
pub fn sub_int(self: State, int: Int) -> State {
  sub_i224(self, int)
}

// ## Transforming

/// Converts a `Bits224` element to a Big-Endian (most-significant bits first) `ByteArray`.
pub fn to_bytearray_big_endian(self: State) -> ByteArray {
  bitwise.to_int(self)
    |> builtin.integer_to_bytearray(True, field_size, _)
}

/// Converts a `Bits224` element to a Little-Endian (least-significant bits first) `ByteArray`.
pub fn to_bytearray_little_endian(self: State) -> ByteArray {
  bitwise.to_int(self)
    |> builtin.integer_to_bytearray(False, field_size, _)
}

/// Converts a `Bits224` element back to its integer representation.
pub fn to_int(self: State) -> Int {
  bitwise.to_int(self)
}

use aiken/builtin
use aiken/crypto/bitwise.{one, zero}
use aiken/crypto/int224.{
  add, add_bytes, add_int, field, from_bytearray_big_endian,
  from_bytearray_little_endian, from_int, mul, mul_bytes, mul_int, neg, scale,
  scale2, sub, sub_bytes, sub_int, to_bytearray_big_endian,
  to_bytearray_little_endian, to_int,
}

test equal_pad_for_addition() {
  let a: ByteArray = #"acab"
  let b: ByteArray = #"cafe"

  let x =
    a
      |> from_bytearray_big_endian
      |> add_bytes(b)
      |> to_bytearray_big_endian

  x == #"000000000000000000000000000000000000000000000000000177a9"
}

test unequal_pad_for_addition() {
  let a: ByteArray = #"acabbeefface"
  let b: ByteArray = #"cafe"

  let x =
    a
      |> from_bytearray_big_endian
      |> add_bytes(b)
      |> to_bytearray_big_endian

  x == #"00000000000000000000000000000000000000000000acabbef0c5cc"
}

// ## Test for constructing Hash224 elements

test from_int_1() {
  and {
    ( from_int(-1) |> to_int ) == field - 1,
    ( from_int(field) |> to_int ) == 0,
    ( from_int(834884848) |> to_int ) == 834884848,
  }
}

test from_bytes_big_endian_1() {
  ( from_bytearray_big_endian(#"ffff00") |> to_int ) == 16776960
}

test from_bytes_little_endian_1() {
  ( from_bytearray_little_endian(#"ffff00") |> to_int ) == 65535
}

// ## Tests for modifying Hash224 elements

test scale_1() {
  let x = from_int(834884848)

  and {
    ( x |> scale(-1) ) == zero,
    ( x |> scale(0) ) == one,
    ( x |> scale(1) ) == x,
    ( x |> scale(2) |> to_int ) == 697032709419983104,
    ( x |> scale(3) |> to_int ) == 581942047655130761945608192,
    (
      from_int(field - 5)
        |> scale(200)
        |> to_int
    ) != 0,
  }
}

test scale2_1() {
  let a = from_int(2)

  and {
    ( scale2(a, 0) |> to_int ) == 2,
    ( scale2(a, 1) |> to_int ) == 4,
    ( scale2(a, 2) |> to_int ) == 16,
    ( scale2(a, 3) |> to_int ) == 256,
  }
}

// ## Tests for combining Hash224 elements

test add_1() {
  let x = from_int(834884848)
  let y = from_int(field - 1)
  let z = from_int(3)

  and {
    (( add(x, x) |> to_int ) == 1669769696)?,
    (add(y, one) == zero)?,
    (add(z, add(y, one)) == z)?,
  }
}

test add_overflow_1() {
  let a = from_int(field - 1)
  let b = from_int(1)

  ( add(a, b) |> to_int ) == 0
}

test mul_1() {
  let x = from_int(834884848)
  and {
    mul(x, x) == from_int(697032709419983104),
    mul(zero, x) == zero,
    mul(from_int(field - 1), from_int(2)) == from_int(field - 2),
  }
}

test neg_1() {
  and {
    neg(from_int(834884848)) == from_int(field - 834884848),
    neg(zero) == zero,
    neg(one) == from_int(field - 1),
  }
}

test sub_1() {
  let x = from_int(834884848)

  and {
    (sub(x, x) == zero)?,
    (sub(zero, from_int(5)) == from_int(field - 5))?,
  }
}

// ## Tests for transforming Hash224 elements

test to_int_1() {
  to_int(from_int(834884848)) == 834884848
}

test to_bytes_1() {
  (
    to_bytearray_big_endian(from_int(16777215))
      |> builtin.slice_bytearray(25, 28, _)
  ) == #"ffffff"
}

test to_bytes_little_endian_1() {
  let value = 16777215
  let bytes = to_bytearray_little_endian(from_int(value))
  and {
    ( bytes |> builtin.slice_bytearray(0, 3, _) ) == #"ffffff",
    ( bytes |> builtin.bytearray_to_integer(False, _) ) == value,
  }
}

// ## Tests for various operations and edge cases

test add_bytes_1() {
  let x = from_int(100)
  let result = add_bytes(x, #"0a") |> to_int
  result == 110
}

test add_int_1() {
  let x = from_int(100)
  let result = add_int(x, 50) |> to_int
  result == 150
}

test mul_bytes_1() {
  let x = from_int(10)
  let result = mul_bytes(x, #"0a") |> to_int
  result == 100
}

test mul_int_1() {
  let x = from_int(10)
  let result = mul_int(x, 5) |> to_int
  result == 50
}

test sub_bytes_1() {
  let x = from_int(100)
  let result = sub_bytes(x, #"0a") |> to_int
  result == 90
}

test sub_int_1() {
  let x = from_int(100)
  let result = sub_int(x, 60) |> to_int
  result == 40
}

test sub_underflow_1() {
  let a = from_int(0)
  let b = from_int(1)

  ( sub(a, b) |> to_int ) == field - 1
}

test overflow_handling() {
  let max = from_int(field - 1)
  and {
    (add_int(max, 1) == zero)?,
    (( add_int(max, 2) |> to_int ) == 1)?,
  }
}

test wraparound_behavior() {
  let x = from_int(10)
  let y = from_int(15)
  and {
    ( sub(x, y) |> to_int ) == field - 5,
    (add(sub(x, y), y) == x)?,
  }
}

test from_bytes_tests() {
  let a = #"acabbeefface"
  let b = #"cafe"

  and {
    ( from_bytearray_big_endian(a) |> to_int ) == 0xacabbeefface,
    ( from_bytearray_big_endian(b) |> to_int ) == 0xcafe,
    ( from_bytearray_little_endian(a) |> to_int ) == 0xcefaefbeabac,
    ( from_bytearray_little_endian(b) |> to_int ) == 0xfeca,
  }
}

test add_tests() {
  let a = from_bytearray_big_endian(#"acab")
  let b = from_bytearray_big_endian(#"cafe")

  and {
    (
      add(a, b) |> to_bytearray_big_endian |> builtin.slice_bytearray(25, 28, _)
    ) == #"0177a9",
    add(from_int(0), from_bytearray_big_endian(#"01")) == from_bytearray_big_endian(
      #"01",
    ),
  }
}

test add_communitive() {
  let a = from_bytearray_big_endian(#"acab")
  let b = from_bytearray_big_endian(#"cafe")

  add(a, b) == add(b, a)
}

test add_associativity() {
  let a = from_bytearray_big_endian(#"0101")
  let b = from_bytearray_big_endian(#"0202")
  let c = from_bytearray_big_endian(#"0303")

  add(add(a, b), c) == add(a, add(b, c))
}

test add_identity() {
  let a = from_bytearray_big_endian(#"01")

  add(zero, a) == a
}

test add_bytes_tests() {
  let a = from_int(0)
  let b = from_bytearray_big_endian(#"acab")

  and {
    (
      add_bytes(a, #"cafe")
        |> to_bytearray_big_endian
        |> builtin.slice_bytearray(26, 28, _)
    ) == #"cafe",
    (
      add_bytes(b, #"cafe")
        |> to_bytearray_big_endian
        |> builtin.slice_bytearray(25, 28, _)
    ) == #"0177a9",
  }
}

test sub_tests() {
  let a = from_bytearray_big_endian(#"0177a9")
  let b = from_bytearray_big_endian(#"cafe")

  and {
    (
      sub(a, b) |> to_bytearray_big_endian |> builtin.slice_bytearray(26, 28, _)
    ) == #"acab",
    (
      sub(
        from_bytearray_big_endian(#"77a9"),
        from_bytearray_big_endian(#"cafe"),
      )
        |> to_bytearray_big_endian
        |> builtin.slice_bytearray(26, 28, _)
    ) == #"acab",
  }
}

test sub_identity() {
  let a = from_bytearray_big_endian(#"10")

  sub(a, zero) == a
}

test mul_tests() {
  let a = from_bytearray_big_endian(#"ffff")
  let b = from_bytearray_big_endian(#"ffff")

  ( mul(a, b) |> to_bytearray_big_endian |> builtin.slice_bytearray(24, 28, _) ) == #"fffe0001"
}

test mul_communitive() {
  let a = from_bytearray_big_endian(#"acab")
  let b = from_bytearray_big_endian(#"cafe")

  mul(a, b) == mul(b, a)
}

test mul_associativity() {
  let a = from_bytearray_big_endian(#"33")
  let b = from_bytearray_big_endian(#"44")
  let c = from_bytearray_big_endian(#"55")

  mul(mul(a, b), c) == mul(a, mul(b, c))
}

test mul_identity() {
  let a = from_bytearray_big_endian(#"acab")

  mul(one, a) == a
}

test mul_bytes_tests() {
  let a = from_int(0)
  let b = from_bytearray_big_endian(#"0002")

  and {
    mul_bytes(a, #"cafe") == zero,
    (
      mul_bytes(b, #"0003")
        |> to_bytearray_big_endian
        |> builtin.slice_bytearray(26, 28, _)
    ) == #"0006",
  }
}

test bytes_conversion_roundtrip() {
  let original = #"acabbeefface"

  and {
    (
      to_bytearray_big_endian(from_bytearray_big_endian(original))
        |> builtin.slice_bytearray(
            28 - builtin.length_of_bytearray(original),
            28,
            _,
          )
    ) == original,
    (
      to_bytearray_little_endian(from_bytearray_little_endian(original))
        |> builtin.slice_bytearray(0, builtin.length_of_bytearray(original), _)
    ) == original,
  }
}

test operation_with_zero() {
  and {
    add(zero, zero) == zero,
    mul(zero, from_int(123456)) == zero,
    sub(zero, zero) == zero,
    neg(zero) == zero,
  }
}

test operation_with_large_numbers() {
  let almost_field_size = from_int(field - 1)
  let large_num = from_int(0xffffffffffffffffffffff)

  and {
    add(almost_field_size, one) == zero,
    mul(large_num, large_num) != zero,
    // Should be non-zero due to modular arithmetic
    neg(almost_field_size) == one,
  }
}

