test includes_5() {
  and {
    after(0) |> includes(after(1)),
    entirely_after(0) |> includes(entirely_after(1)),
  }
}

test includes_6() {
  and {
    after(0) |> includes(after(1)),
    entirely_after(0) |> includes(after(1)),
  }
}

test includes_7() {
  and {
    before(0) |> includes(before(-1)),
    entirely_before(0) |> includes(before(-1)),
  }
}

