pub fn flatten(self: Value) -> List<(PolicyId, AssetName, Int)> {
  dict.foldr(
    self.inner,
    [],
    fn(policy_id, asset_list, value) {
      dict.foldr(
        asset_list,
        value,
        fn(asset_name, quantity, xs) {
          [(policy_id, asset_name, quantity), ..xs]
        },
      )
    },
  )
}

pub fn flatten_with(self: Value, with: FlattenStrategy<result>) -> List<result> {
  dict.foldr(
    self.inner,
    [],
    fn(policy_id, asset_list, value) {
      dict.foldr(
        asset_list,
        value,
        fn(asset_name, quantity, xs) {
          with(
            policy_id,
            asset_name,
            quantity,
            fn(x) { [x, ..xs] },
            fn() { xs },
          )
        },
      )
    },
  )
}

tests:
test flatten_with_2() {
  let v =
    zero
      |> add("a", "1", 14)
      |> add("b", "", 42)
      |> add("a", "2", 42)

  flatten_with(
    v,
    fn(p, a, q, keep, discard) {
      if q == 42 {
        keep((p, a))
      } else {
        discard()
      }
    },
  ) == [("a", "2"), ("b", "")]
}

