/// Combine two `Value` together.
pub fn merge(left v0: Value, right v1: Value) -> Value {
  Value(
    dict.union_with(
      v0.inner,
      v1.inner,
      fn(_, a0, a1, keep, discard) {
        let result = dict.union_with(a0, a1, dict_strategy.sum_if_non_zero())
        if dict.is_empty(result) {
          discard()
        } else {
          keep(result)
        }
      },
    ),
  )
}

