/// <small>Minimum Plutus Version</small> | <small>1</small>
/// ---                                   | ---
///
/// Interpret a `Data` as a constructor. Fails if it's not a constructor.
pub fn un_constr_data(data: Data) -> Pair<Int, List<Data>> {
  fail
