modules.content:
//// Since transaction size is limited in Cardano, some validators benefit from a
//// solution which allows them to delegate parts of their logics. This becomes more
//// prominent in cases where such logics can greatly benefit from optimization
//// solutions that trade computation resources for script sizes (e.g. table
//// lookups can take up more space so that costly computations can be averted).
//// 
//// This design pattern offers an interface for off-loading such logics into an
//// external withdrawal script, so that the size of the validator itself can stay
//// within the limits of Cardano.
//// 
//// > [!NOTE]
//// > Be aware that total size of reference scripts is currently limited to 200KiB
//// > (204800 bytes), and they also impose additional fees in an exponential manner.
//// > See [here](https://github.com/IntersectMBO/cardano-ledger/issues/3952) and [here](https://github.com/CardanoSolutions/ogmios/releases/tag/v6.5.0) for
//// > more info.

use aiken/builtin
use aiken/collection/pairs
use aiken/crypto.{ScriptHash, blake2b_224}
use aiken/fuzz
use aiken_design_patterns/utils.{sum_of_squares}
use cardano/address.{Script}
use cardano/transaction.{Redeemer, ScriptPurpose, Withdraw}

/// Datatype for redeemer of the "computation stake validator" to represent
/// input argument(s) and output value(s).
///
/// As a simple example, a summation logic where it adds all its inputs
/// together, can work with a redeemer of
/// type `WithdrawRedeemerIO<List<Int>, Int>`, and a valid redeemer data would
/// be:
/// ```aiken
/// let valid_summation_io =
///   WithdrawRedeemerIO {
///     input_arg: [1, 2, 3, 4, 5],
///     result: 15,
///   }
/// ```
pub type WithdrawRedeemerIO<a, b> {
  input_arg: a,
  result: b,
}

/// Datatype for a delegated validation. Compared to `WithdrawRedeemerIO`, this
/// datatype only carries input argument(s), and simply validates whether the
/// computation passes.
pub type WithdrawRedeemer<a> {
  input_arg: a,
}

/// Given an arbitrary `Data` as input, this function expects to find
/// a `Withdraw` script purpose in `redeemers` for `staking_validator`, with a
/// redeemer of type [`WithdrawRedeemer<Data, Data>`](#withdrawredeemera-b),
/// which will be coerced into your custom datatypes using your provided `Data`
/// validators (`input_data_coercer` and `output_data_coercer`).
pub fn delegated_compute(
  function_input: a,
  staking_validator: ScriptHash,
  input_data_coercer: fn(Data) -> a,
  output_data_coercer: fn(Data) -> b,
  redeemers: Pairs<ScriptPurpose, Redeemer>,
) -> b {
  expect Some(rdmr) =
    redeemers
      |> pairs.get_first(Withdraw(Script(staking_validator)))
  expect WithdrawRedeemerIO { input_arg, result }: WithdrawRedeemerIO<
    Data,
    Data,
  > = rdmr

  let coerced_input = input_data_coercer(input_arg)

  // Given input argument must be identical to the one provided to the
  // withdrawal validator.
  expect (coerced_input == function_input)?

  // Return coerced result
  output_data_coercer(result)
}

/// Similar to [`delegated_compute`](#delegated_compute), with the difference
/// that now values are expected to be returned by the staking script.
pub fn delegated_validation(
  function_input: a,
  staking_validator: ScriptHash,
  input_data_coercer: fn(Data) -> a,
  redeemers: Pairs<ScriptPurpose, Redeemer>,
) -> Bool {
  expect Some(rdmr) =
    redeemers
      |> pairs.get_first(Withdraw(Script(staking_validator)))
  expect WithdrawRedeemer { input_arg }: WithdrawRedeemer<Data> = rdmr

  let coerced_input = input_data_coercer(input_arg)

  // Given input argument must be identical to the one provided to the
  // withdrawal validator.
  coerced_input == function_input
}

pub fn generic_delegated_validation(
  staking_validator: ScriptHash,
  withdraw_redeemer_validator: fn(Data) -> Bool,
  redeemers: Pairs<ScriptPurpose, Redeemer>,
) -> Bool {
  expect Some(rdmr) =
    redeemers
      |> pairs.get_first(Withdraw(Script(staking_validator)))
  withdraw_redeemer_validator(rdmr)
}

/// Helper function for defining your "computation stake validator." The
/// resulting stake validator will carry out the provided `function`'s logic,
/// and `redeemer` must contain the input(s) and expected output(s).
pub fn withdraw_io(
  function: fn(a) -> b,
  redeemer: WithdrawRedeemerIO<a, b>,
) -> Bool {
  let WithdrawRedeemerIO { input_arg, result } = redeemer
  let computed_result = function(input_arg)
  result == computed_result
}

/// Helper function for defining your delegated validation. The resulting stake
/// validator will carry out the provided `validation`'s logic with given
/// input(s) through its redeemer.
pub fn withdraw(
  validation: fn(a) -> Bool,
  redeemer: WithdrawRedeemer<a>,
) -> Bool {
  let WithdrawRedeemer { input_arg } = redeemer
  validation(input_arg)
}

fn data_to_ints(d: Data) -> List<Int> {
  expect ints: List<Int> = d
  ints
}

test success_delegated_compute(script_hash via fuzz.bytearray_fixed(28)) {
  let redeemer = WithdrawRedeemerIO([0, 1, 2, 3, 4], 30)
  let redeemer_data: Data = redeemer
  let result =
    delegated_compute(
      [0, 1, 2, 3, 4],
      script_hash,
      data_to_ints,
      builtin.un_i_data,
      [Pair(Withdraw(Script(script_hash)), redeemer_data)],
    )
  and {
    result == 30,
    withdraw_io(sum_of_squares, redeemer),
  }
}

test fail_delegated_compute(script_hash via fuzz.bytearray_fixed(28)) fail {
  let redeemer = WithdrawRedeemerIO([0, 1, 2, 3, 4], 30)
  let redeemer_data: Data = redeemer
  let result =
    delegated_compute(
      [0, 1, 2, 3, 4],
      script_hash,
      data_to_ints,
      builtin.un_i_data,
      [Pair(Withdraw(Script(blake2b_224(script_hash))), redeemer_data)],
    )
  and {
    result == 30,
    withdraw_io(sum_of_squares, redeemer),
  }
}

test success_delegated_validation(script_hash via fuzz.bytearray_fixed(28)) {
  let redeemer = WithdrawRedeemer(42)
  let redeemer_data: Data = redeemer
  and {
    delegated_validation(
      42,
      script_hash,
      builtin.un_i_data,
      [Pair(Withdraw(Script(script_hash)), redeemer_data)],
    ),
    withdraw(fn(x: Int) { x == 42 }, redeemer),
  }
}

test fail_delegated_validation(script_hash via fuzz.bytearray_fixed(28)) fail {
  let redeemer = WithdrawRedeemer(42)
  let redeemer_data: Data = redeemer
  and {
    delegated_validation(
      42,
      script_hash,
      builtin.un_i_data,
      [Pair(Withdraw(Script(blake2b_224(script_hash))), redeemer_data)],
    ),
    withdraw(fn(x: Int) { x == 42 }, redeemer),
  }
}

test success_generic_delegated_validation(
  script_hash via fuzz.bytearray_fixed(28),
) {
  let redeemer_data: Data = builtin.i_data(42)
  generic_delegated_validation(
    script_hash,
    fn(rdmr: Data) -> Bool {
      let i = builtin.un_i_data(rdmr)
      i == 42
    },
    [Pair(Withdraw(Script(script_hash)), redeemer_data)],
  )
}

test fail_generic_delegated_validation(
  script_hash via fuzz.bytearray_fixed(28),
) fail {
  let redeemer_data: Data = builtin.i_data(42)
  generic_delegated_validation(
    script_hash,
    fn(rdmr: Data) -> Bool {
      let i = builtin.un_i_data(rdmr)
      i == 42
    },
    [Pair(Withdraw(Script(blake2b_224(script_hash))), redeemer_data)],
  )
}

functions.implementation:
pub fn withdraw_no_redeemer(
  input_output_validator: fn(Int, Input, Int, Output) -> Bool,
  input_collective_outputs_validator: fn(Int, Input, List<Output>) -> Bool,
  collective_inputs_outputs_validator: fn(List<Input>, List<Output>) -> Bool,
  outer_indices: Pairs<Int, List<Int>>,
  total_input_count: Int,
  stake_cred: Credential,
  tx: Transaction,
) -> Bool {
  stake_validator.withdraw(
    fn(indices, own_validator, tx) {
      let Transaction { inputs, outputs, .. } = tx
      let
        processed_indices,
        _,
        _,
        all_inputs,
        all_outputs,
        remaining_script_inputs_count,
      <-
        list_extra.foldr6(
          inputs,
          list.reverse(indices),
          total_input_count,
          511,
          [],
          [],
          total_input_count - 1,
          fn(
            input,
            remaining_indices,
            in0,
            out0,
            all_inputs_so_far,
            all_outputs_so_far,
            i,
            return,
          ) {
            let next_i = i - 1
            when input.output.address.payment_credential is {
              Script(script) ->
                if script == own_validator {
                  when remaining_indices is {
                    [] -> fail @"More UTxOs are spent than specified"
                    [Pair(in1, outs), ..rest_of_indices] ->
                      if i == in1 && in1 < in0 {
                        let
                          new_latest_out_ix,
                          collected_outputs,
                          all_outputs_so_far_updated,
                        <-
                          list_extra.foldr3(
                            outs,
                            out0,
                            [],
                            all_outputs_so_far,
                            fn(
                              curr_out_ix,
                              prev_out_ix,
                              outputs_so_far,
                              outer_all_outputs,
                              inner_return,
                            ) {
                              if curr_out_ix < prev_out_ix {
                                expect Some(out_utxo) =
                                  outputs |> list.at(curr_out_ix)
                                if input_output_validator(
                                  in1,
                                  input,
                                  curr_out_ix,
                                  out_utxo,
                                ) {
                                  inner_return(
                                    curr_out_ix,
                                    list.push(outputs_so_far, out_utxo),
                                    list.push(outer_all_outputs, out_utxo),
                                  )
                                } else {
                                  fail @"Validation on an input with one of its corresponding outputs failed"
                                }
                              } else {
                                fail @"All output indices must be in ascending order"
                              }
                            },
                          )
                        if input_collective_outputs_validator(
                          in1,
                          input,
                          collected_outputs,
                        ) {
                          return(
                            rest_of_indices,
                            in1,
                            new_latest_out_ix,
                            list.push(all_inputs_so_far, input),
                            all_outputs_so_far_updated,
                            next_i,
                          )
                        } else {
                          fail @"Validation on the output count failed"
                        }
                      } else {
                        fail @"Input and output indices must be in ascending orders"
                      }
                  }
                } else {
                  return(
                    remaining_indices,
                    in0,
                    out0,
                    all_inputs_so_far,
                    all_outputs_so_far,
                    next_i,
                  )
                }
              _ ->
                return(
                  remaining_indices,
                  in0,
                  out0,
                  all_inputs_so_far,
                  all_outputs_so_far,
                  next_i,
                )
            }
          },
        )
      and {
        collective_inputs_outputs_validator(all_inputs, all_outputs),
        list.is_empty(processed_indices),
        remaining_script_inputs_count == -1,
      }
    },
    outer_indices,
    stake_cred,
    tx,
  )
}

pub fn withdraw_with_redeemer(
  spend_redeemer_coercer_and_stake_credential_extractor: fn(Redeemer) ->
    (a, Credential),
  input_output_validator: fn(Int, Input, a, Int, Output) -> Bool,
  input_collective_outputs_validator: fn(Int, Input, a, List<Output>) -> Bool,
  collective_inputs_outputs_validator: fn(List<Input>, List<Output>) -> Bool,
  indices: Pairs<Int, List<Int>>,
  total_script_inputs: Int,
  stake_cred: Credential,
  tx: Transaction,
) -> Bool {
  let Transaction { inputs, outputs, redeemers, .. } = tx
  // Folding from left so that redeemers are ordered in reverse. This allows
  // for cheaper collection of UTxOs in the next fold.
  let script_redeemers =
    list.foldl(
      redeemers,
      [],
      fn(redeemer, acc) {
        let Pair(redeemer_purpose, redeemer_data) = redeemer
        when redeemer_purpose is {
          Spend(_) -> {
            let (coerced_redeemer, stake_credential_from_spend_redeemer) =
              spend_redeemer_coercer_and_stake_credential_extractor(
                redeemer_data,
              )
            if stake_cred == stake_credential_from_spend_redeemer {
              list.push(acc, coerced_redeemer)
            } else {
              acc
            }
          }
          _ -> acc
        }
      },
    )
  let
    processed_redeemers,
    _,
    _,
    all_inputs,
    all_outputs,
    remaining_script_inputs_count,
  <-
    list_extra.foldr6(
      indices,
      script_redeemers,
      511,
      511,
      [],
      [],
      total_script_inputs - 1,
      fn(
        curr_input_outputs_indices,
        remaining_redeemers,
        in0,
        out0,
        all_inputs_so_far,
        all_outputs_so_far,
        i,
        return,
      ) {
        let next_i = i - 1
        let Pair(in1, outs) = curr_input_outputs_indices
        if in1 < in0 {
          expect Some(in_utxo) = inputs |> list.at(in1)
          when remaining_redeemers is {
            [] -> fail @"More UTxOs are spent than there are redeemers"
            [current_redeemer, ..rest_of_redeemers] -> {
              let
                new_latest_out_ix,
                collected_outputs,
                all_outputs_so_far_updated,
              <-
                list_extra.foldr3(
                  outs,
                  out0,
                  [],
                  all_outputs_so_far,
                  fn(
                    curr_out_ix,
                    prev_out_ix,
                    outputs_so_far,
                    outer_all_outputs,
                    inner_return,
                  ) {
                    if curr_out_ix < prev_out_ix {
                      expect Some(out_utxo) = outputs |> list.at(curr_out_ix)
                      if input_output_validator(
                        in1,
                        in_utxo,
                        current_redeemer,
                        curr_out_ix,
                        out_utxo,
                      ) {
                        inner_return(
                          curr_out_ix,
                          list.push(outputs_so_far, out_utxo),
                          list.push(outer_all_outputs, out_utxo),
                        )
                      } else {
                        fail @"Validation on an input with one of its corresponding outputs failed"
                      }
                    } else {
                      fail @"All output indices must be in ascending order"
                    }
                  },
                )
              if input_collective_outputs_validator(
                in1,
                in_utxo,
                current_redeemer,
                collected_outputs,
              ) {
                return(
                  rest_of_redeemers,
                  in1,
                  new_latest_out_ix,
                  list.push(all_inputs_so_far, in_utxo),
                  all_outputs_so_far_updated,
                  next_i,
                )
              } else {
                fail @"Validation on the output count failed"
              }
            }
          }
        } else {
          fail @"All input indices must be in ascending order"
        }
      },
    )
  and {
    collective_inputs_outputs_validator(all_inputs, all_outputs),
    list.is_empty(processed_redeemers),
    remaining_script_inputs_count == -1,
  }
}

atoms.implementation:
fn tests_fuzzer() -> Fuzzer<
  (
    ScriptHash,
    List<Input>,
    List<Output>,
    Pairs<Int, List<Int>>,
    Int,
    Pairs<ScriptPurpose, Redeemer>,
  ),
> {
  let script_hash <- fuzz.and_then(fuzz.bytearray_fixed(28))
  let wallet_inputs <- fuzz.and_then(utils.user_inputs_fuzzer())
  let script_inputs <-
    fuzz.and_then(
      fuzz.list_between(
        utils.specific_script_input_fuzzer(script_hash, NoDatum),
        1,
        10,
      ),
    )
  let inputs = list.concat(wallet_inputs, script_inputs) |> utils.sort_inputs
  let (script_input_indices, script_outputs) =
    list.indexed_foldr(
      inputs,
      ([], []),
      fn(i, input, acc) {
        let (i_indices, outputs) = acc
        when input.output.address.payment_credential is {
          Script(_) ->
            (
              i_indices |> list.push(i),
              outputs
                |> list.push(input.output)
                |> list.push(input.output)
                |> list.push(input.output)
                |> list.push(input.output)
                |> list.push(input.output),
            )
          _ -> acc
        }
      },
    )
  let redeemer =
    script_input_indices
      |> list.indexed_foldr(
          [],
          fn(i, script_input_index, acc) {
            list.push(
              acc,
              Pair(script_input_index, list.range(i * 5, i * 5 + 4)),
            )
          },
        )
  fuzz.constant(
    (
      script_hash,
      inputs,
      script_outputs,
      redeemer,
      list.length(script_inputs),
      list.map(
        script_inputs,
        fn(i) { Pair(Spend(i.output_reference), builtin.i_data(0)) },
      ),
    ),
  )
}

