pub fn sum_if_non_zero() -> UnionStrategy<key, Int> {
  fn(_key, left, right, keep, discard) {
    let value = left + right
    if value == 0 {
      discard()
    } else {
      keep(value)
    }
  }
}

pub fn from_ascending_pairs(xs: Pairs<ByteArray, value>) -> Dict<key, value> {
