}

test insert_with_2() {
  let result =
    empty
      |> insert_with(key: "foo", value: 1, with: strategy.sum())
      |> insert_with(key: "bar", value: 2, with: strategy.sum())
      |> insert_with(key: "foo", value: 3, with: strategy.sum())
      |> to_pairs()

  result == [Pair("bar", 2), Pair("foo", 4)]
