test insert_with_3() {
  let with =
    fn(k, a, _b, keep, discard) {
      if k == "foo" {
        keep(a)
      } else {
        discard()
      }
    }

  let result =
    empty
      |> insert_with(key: "foo", value: 1, with: with)
      |> insert_with(key: "bar", value: 2, with: with)
      |> insert_with(key: "foo", value: 3, with: with)
      |> insert_with(key: "bar", value: 4, with: with)
      |> to_pairs()

  result == [Pair("foo", 1)]
}

