pub fn delete_last(self: Pairs<key, value>, key k: key) -> Pairs<key, value> {
  when self is {
    [] -> []
    [Pair(k2, v2), ..rest] ->
      if k == k2 {
        let tail = delete_last(rest, k)
        if tail == rest {
          rest
        } else {
          [Pair(k2, v2), ..tail]
        }
      } else {
        [Pair(k2, v2), ..delete_last(rest, k)]
      }
  }
}

