pub fn insert_with_by_ascending_key(
  self: Pairs<key, value>,
  key k: key,
  value v: value,
  compare: fn(key, key) -> Ordering,
  with: fn(value, value) -> value,
) -> Pairs<key, value> {
  when self is {
    [] -> [Pair(k, v)]
    [Pair(k2, v2), ..rest] ->
      if compare(k, k2) == Less {
        [Pair(k, v), ..self]
      } else {
        if k == k2 {
          [Pair(k, with(v, v2)), ..rest]
        } else {
          [
            Pair(k2, v2),
            ..insert_with_by_ascending_key(rest, k, v, compare, with)
          ]
        }
      }
  }
}

tests:
test insert_with_by_ascending_key_1() {
  let compare_un_b_data =
    fn(l, r) {
      bytearray.compare(l |> builtin.un_b_data, r |> builtin.un_b_data)
    }

  let m =
    []
      |> insert_with_by_ascending_key(
          "foo" |> builtin.b_data,
          42,
          compare_un_b_data,
          builtin.add_integer,
        )
      |> insert_with_by_ascending_key(
          "foo" |> builtin.b_data,
          14,
          compare_un_b_data,
          builtin.add_integer,
        )

  m == [Pair("foo" |> builtin.b_data, 56)]
}

test insert_with_by_ascending_key_2() {
  let compare_un_b_data =
    fn(l, r) {
      bytearray.compare(l |> builtin.un_b_data, r |> builtin.un_b_data)
    }

  let m =
    []
      |> insert_with_by_ascending_key(
          "foo" |> builtin.b_data,
          42,
          compare_un_b_data,
          builtin.add_integer,
        )
      |> insert_with_by_ascending_key(
          "bar" |> builtin.b_data,
          14,
          compare_un_b_data,
          builtin.add_integer,
        )
      |> insert_with_by_ascending_key(
          "baz" |> builtin.b_data,
          1337,
          compare_un_b_data,
          builtin.add_integer,
        )

  m == [
    Pair("bar" |> builtin.b_data, 14),
    Pair("baz" |> builtin.b_data, 1337),
    Pair("foo" |> builtin.b_data, 42),
  ]
}

test insert_with_by_ascending_key_3() {
  let compare_un_b_data =
    fn(l, r) {
      bytearray.compare(l |> builtin.un_b_data, r |> builtin.un_b_data)
    }

  let result =
    []
      |> insert_with_by_ascending_key(
          "foo" |> builtin.b_data,
          1,
          compare_un_b_data,
          builtin.add_integer,
        )
      |> insert_with_by_ascending_key(
          "bar" |> builtin.b_data,
          2,
          compare_un_b_data,
          builtin.add_integer,
        )
      |> insert_with_by_ascending_key(
          "foo" |> builtin.b_data,
          3,
          compare_un_b_data,
          builtin.add_integer,
        )

  result == [Pair("bar" |> builtin.b_data, 2), Pair("foo" |> builtin.b_data, 4)]
}

