pub fn max(left: IntervalBound, right: IntervalBound) -> IntervalBound {
  compare_bound_type(
    left.bound_type,
    right.bound_type,
    fn() { right },
    fn(is_negative) {
      if left.is_inclusive == right.is_inclusive {
        left
      } else if is_negative {
        if left.is_inclusive {
          right
        } else {
          left
        }
      } else {
        if left.is_inclusive {
          left
        } else {
          right
        }
      }
    },
    fn() { left },
  )
}

pub fn min(left: IntervalBound, right: IntervalBound) -> IntervalBound {
  compare_bound_type(
    left.bound_type,
    right.bound_type,
    fn() { left },
    fn(is_negative) {
      if left.is_inclusive == right.is_inclusive {
        left
      } else if is_negative {
        if left.is_inclusive {
          left
        } else {
          right
        }
      } else {
        if left.is_inclusive {
          right
        } else {
          left
        }
      }
    },
    fn() { right },
  )
}

