  let y = ratio(2, 1)
  expect Some(z) = arithmetic_mean([x, y])
  reduce(z) == ratio(3, 2)
}

test arithmetic_mean_3() {
  let xs =
    [
      ratio(1, 1),
      ratio(2, 1),
      ratio(3, 1),
      ratio(4, 1),
      ratio(5, 1),
      ratio(6, 1),
    ]
  expect Some(z) = arithmetic_mean(xs)
