pub fn delegated_compute(
  function_input: a,
  staking_validator: ScriptHash,
  input_data_coercer: fn(Data) -> a,
  output_data_coercer: fn(Data) -> b,
  redeemers: Pairs<ScriptPurpose, Redeemer>,
) -> b {
  expect Some(rdmr) =
    redeemers
      |> pairs.get_first(Withdraw(Script(staking_validator)))
  expect WithdrawRedeemerIO { input_arg, result }: WithdrawRedeemerIO<
    Data,
    Data,
  > = rdmr

  let coerced_input = input_data_coercer(input_arg)

  // Given input argument must be identical to the one provided to the
  // withdrawal validator.
  expect (coerced_input == function_input)?

  // Return coerced result
  output_data_coercer(result)
}

pub fn delegated_validation(
  function_input: a,
  staking_validator: ScriptHash,
  input_data_coercer: fn(Data) -> a,
  redeemers: Pairs<ScriptPurpose, Redeemer>,
) -> Bool {
  expect Some(rdmr) =
    redeemers
      |> pairs.get_first(Withdraw(Script(staking_validator)))
  expect WithdrawRedeemer { input_arg }: WithdrawRedeemer<Data> = rdmr

  let coerced_input = input_data_coercer(input_arg)

  // Given input argument must be identical to the one provided to the
  // withdrawal validator.
  coerced_input == function_input
}

pub fn generic_delegated_validation(
  staking_validator: ScriptHash,
  withdraw_redeemer_validator: fn(Data) -> Bool,
  redeemers: Pairs<ScriptPurpose, Redeemer>,
) -> Bool {
  expect Some(rdmr) =
    redeemers
      |> pairs.get_first(Withdraw(Script(staking_validator)))
  withdraw_redeemer_validator(rdmr)
}

pub fn withdraw_io(
  function: fn(a) -> b,
  redeemer: WithdrawRedeemerIO<a, b>,
) -> Bool {
  let WithdrawRedeemerIO { input_arg, result } = redeemer
  let computed_result = function(input_arg)
  result == computed_result
}

pub fn withdraw(
  validation: fn(a) -> Bool,
  redeemer: WithdrawRedeemer<a>,
) -> Bool {
  let WithdrawRedeemer { input_arg } = redeemer
  validation(input_arg)
}

pub fn withdraw_no_redeemer(
  input_output_validator: fn(Int, Input, Int, Output) -> Bool,
  input_collective_outputs_validator: fn(Int, Input, List<Output>) -> Bool,
  collective_inputs_outputs_validator: fn(List<Input>, List<Output>) -> Bool,
  outer_indices: Pairs<Int, List<Int>>,
  total_input_count: Int,
  stake_cred: Credential,
  tx: Transaction,
) -> Bool {
  stake_validator.withdraw(
    fn(indices, own_validator, tx) {
      let Transaction { inputs, outputs, .. } = tx
      let
        processed_indices,
        _,
        _,
        all_inputs,
        all_outputs,
        remaining_script_inputs_count,
      <-
        list_extra.foldr6(
          inputs,
          list.reverse(indices),
          total_input_count,
          511,
          [],
          [],
          total_input_count - 1,
          fn(
            input,
            remaining_indices,
            in0,
            out0,
            all_inputs_so_far,
            all_outputs_so_far,
            i,
            return,
          ) {
            let next_i = i - 1
            when input.output.address.payment_credential is {
              Script(script) ->
                if script == own_validator {
                  when remaining_indices is {
                    [] -> fail @"More UTxOs are spent than specified"
                    [Pair(in1, outs), ..rest_of_indices] ->
                      if i == in1 && in1 < in0 {
                        let
                          new_latest_out_ix,
                          collected_outputs,
                          all_outputs_so_far_updated,
                        <-
                          list_extra.foldr3(
                            outs,
                            out0,
                            [],
                            all_outputs_so_far,
                            fn(
                              curr_out_ix,
                              prev_out_ix,
                              outputs_so_far,
                              outer_all_outputs,
                              inner_return,
                            ) {
                              if curr_out_ix < prev_out_ix {
                                expect Some(out_utxo) =
                                  outputs |> list.at(curr_out_ix)
                                if input_output_validator(
                                  in1,
                                  input,
                                  curr_out_ix,
                                  out_utxo,
                                ) {
                                  inner_return(
                                    curr_out_ix,
                                    list.push(outputs_so_far, out_utxo),
                                    list.push(outer_all_outputs, out_utxo),
                                  )
                                } else {
                                  fail @"Validation on an input with one of its corresponding outputs failed"
                                }
                              } else {
                                fail @"All output indices must be in ascending order"
                              }
                            },
                          )
                        if input_collective_outputs_validator(
                          in1,
                          input,
                          collected_outputs,
                        ) {
                          return(
                            rest_of_indices,
                            in1,
                            new_latest_out_ix,
                            list.push(all_inputs_so_far, input),
                            all_outputs_so_far_updated,
                            next_i,
                          )
                        } else {
                          fail @"Validation on the output count failed"
                        }
                      } else {
                        fail @"Input and output indices must be in ascending orders"
                      }
                  }
                } else {
                  return(
                    remaining_indices,
                    in0,
                    out0,
                    all_inputs_so_far,
                    all_outputs_so_far,
                    next_i,
                  )
                }
              _ ->
                return(
                  remaining_indices,
                  in0,
                  out0,
                  all_inputs_so_far,
                  all_outputs_so_far,
                  next_i,
                )
            }
          },
        )
      and {
        collective_inputs_outputs_validator(all_inputs, all_outputs),
        list.is_empty(processed_indices),
        remaining_script_inputs_count == -1,
      }
    },
    outer_indices,
    stake_cred,
    tx,
  )
}

pub fn withdraw_with_redeemer(
  spend_redeemer_coercer_and_stake_credential_extractor: fn(Redeemer) ->
    (a, Credential),
  input_output_validator: fn(Int, Input, a, Int, Output) -> Bool,
  input_collective_outputs_validator: fn(Int, Input, a, List<Output>) -> Bool,
  collective_inputs_outputs_validator: fn(List<Input>, List<Output>) -> Bool,
  indices: Pairs<Int, List<Int>>,
  total_script_inputs: Int,
  stake_cred: Credential,
  tx: Transaction,
) -> Bool {
  let Transaction { inputs, outputs, redeemers, .. } = tx
  // Folding from left so that redeemers are ordered in reverse. This allows
  // for cheaper collection of UTxOs in the next fold.
  let script_redeemers =
    list.foldl(
      redeemers,
      [],
      fn(redeemer, acc) {
        let Pair(redeemer_purpose, redeemer_data) = redeemer
        when redeemer_purpose is {
          Spend(_) -> {
            let (coerced_redeemer, stake_credential_from_spend_redeemer) =
              spend_redeemer_coercer_and_stake_credential_extractor(
                redeemer_data,
              )
            if stake_cred == stake_credential_from_spend_redeemer {
              list.push(acc, coerced_redeemer)
            } else {
              acc
            }
          }
          _ -> acc
        }
      },
    )
  let
    processed_redeemers,
    _,
    _,
    all_inputs,
    all_outputs,
    remaining_script_inputs_count,
  <-
    list_extra.foldr6(
      indices,
      script_redeemers,
      511,
      511,
      [],
      [],
      total_script_inputs - 1,
      fn(
        curr_input_outputs_indices,
        remaining_redeemers,
        in0,
        out0,
        all_inputs_so_far,
        all_outputs_so_far,
        i,
        return,
      ) {
        let next_i = i - 1
        let Pair(in1, outs) = curr_input_outputs_indices
        if in1 < in0 {
          expect Some(in_utxo) = inputs |> list.at(in1)
          when remaining_redeemers is {
            [] -> fail @"More UTxOs are spent than there are redeemers"
            [current_redeemer, ..rest_of_redeemers] -> {
              let
                new_latest_out_ix,
                collected_outputs,
                all_outputs_so_far_updated,
              <-
                list_extra.foldr3(
                  outs,
                  out0,
                  [],
                  all_outputs_so_far,
                  fn(
                    curr_out_ix,
                    prev_out_ix,
                    outputs_so_far,
                    outer_all_outputs,
                    inner_return,
                  ) {
                    if curr_out_ix < prev_out_ix {
                      expect Some(out_utxo) = outputs |> list.at(curr_out_ix)
                      if input_output_validator(
                        in1,
                        in_utxo,
                        current_redeemer,
                        curr_out_ix,
                        out_utxo,
                      ) {
                        inner_return(
                          curr_out_ix,
                          list.push(outputs_so_far, out_utxo),
                          list.push(outer_all_outputs, out_utxo),
                        )
                      } else {
                        fail @"Validation on an input with one of its corresponding outputs failed"
                      }
                    } else {
                      fail @"All output indices must be in ascending order"
                    }
                  },
                )
              if input_collective_outputs_validator(
                in1,
                in_utxo,
                current_redeemer,
                collected_outputs,
              ) {
                return(
                  rest_of_redeemers,
                  in1,
                  new_latest_out_ix,
                  list.push(all_inputs_so_far, in_utxo),
                  all_outputs_so_far_updated,
                  next_i,
                )
              } else {
                fail @"Validation on the output count failed"
              }
            }
          }
        } else {
          fail @"All input indices must be in ascending order"
        }
      },
    )
  and {
    collective_inputs_outputs_validator(all_inputs, all_outputs),
    list.is_empty(processed_redeemers),
    remaining_script_inputs_count == -1,
  }
}

pub fn withdraw_no_redeemer(
  validation_logic: fn(Int, Input, Int, Output) -> Bool,
  indices: Pairs<Int, Int>,
  stake_cred: Credential,
  tx: Transaction,
) -> Bool {
  stake_validator.withdraw(
    fn(indices, own_validator, tx) {
      let Transaction { inputs, outputs, .. } = tx
      let
        processed_indices,
        _,
        _,
        _,
      <-
        list_extra.foldl4(
          inputs,
          indices,
          -1,
          -1,
          0,
          fn(input, remaining_indices, in0, out0, i, return) {
            let next_i = i + 1
            when input.output.address.payment_credential is {
              Script(script) ->
                if script == own_validator {
                  when remaining_indices is {
                    [] -> fail @"More UTxOs are spent than specified"
                    [Pair(in1, out1), ..rest_of_indices] ->
                      if i == in1 && in1 > in0 && out1 > out0 {
                        expect Some(out_utxo) = outputs |> list.at(out1)
                        if validation_logic(in1, input, out1, out_utxo) {
                          return(rest_of_indices, in1, out1, next_i)
                        } else {
                          fail @"Validation failed"
                        }
                      } else {
                        fail @"Input and output indices must be in ascending orders"
                      }
                  }
                } else {
                  return(remaining_indices, in0, out0, next_i)
                }
              _ -> return(remaining_indices, in0, out0, next_i)
            }
          },
        )
      (processed_indices == [])?
    },
    indices,
    stake_cred,
    tx,
  )
}

pub fn withdraw_with_redeemer(
  spend_redeemer_coercer_and_stake_credential_extractor: fn(Data) ->
    (a, Credential),
  validation_logic: fn(Int, Input, a, Int, Output) -> Bool,
  indices: Pairs<Int, Int>,
  stake_cred: Credential,
  tx: Transaction,
) -> Bool {
  let Transaction { inputs, outputs, redeemers, .. } = tx
  let script_redeemers =
    list.foldr(
      redeemers,
      [],
      fn(redeemer, acc) {
        let Pair(redeemer_purpose, redeemer_data) = redeemer
        when redeemer_purpose is {
          Spend(_) -> {
            let (coerced_redeemer, stake_credential_from_spend_redeemer) =
              spend_redeemer_coercer_and_stake_credential_extractor(
                redeemer_data,
              )
            if stake_cred == stake_credential_from_spend_redeemer {
              list.push(acc, coerced_redeemer)
            } else {
              acc
            }
          }
          _ -> acc
        }
      },
    )
  let
    processed_redeemers,
    _,
    _,
    _,
  <-
    list_extra.foldl4(
      indices,
      script_redeemers,
      -1,
      -1,
      0,
      fn(curr_in_out_indices, remaining_script_redeemers, in0, out0, i, return) {
        when remaining_script_redeemers is {
          [] -> fail @"Too many indices specified"
          [script_redeemer, ..rest_of_script_redeemers] -> {
            let Pair(in1, out1) = curr_in_out_indices
            if in1 > in0 && out1 > out0 {
              expect Some(in_utxo) = inputs |> list.at(in1)
              expect Some(out_utxo) = outputs |> list.at(out1)
              if validation_logic(in1, in_utxo, script_redeemer, out1, out_utxo) {
                return(rest_of_script_redeemers, in1, out1, i + 1)
              } else {
                fail @"Validation failed"
              }
            } else {
              // Input and output indices must be ordered to disallow duplicates.
              fail @"Input and output indices must be in ascending orders"
            }
          }
        }
      },
    )
  (processed_redeemers == [])?
}

pub fn apply_param(
  version: Int,
  prefix: ByteArray,
  param: ByteArray,
) -> ScriptHash {
  append_first_param_to_prefix(version, prefix, param)
    |> bytearray.concat(postfix)
    |> blake2b_224
}

pub fn apply_prehashed_param(
  version: Int,
  prefix: ByteArray,
  param: ByteArray,
) -> ScriptHash {
  append_first_prehashed_param_to_prefix(version, prefix, param)
    |> bytearray.concat(postfix)
    |> blake2b_224
}

