pub fn one_to_many(
  input_output_validator: fn(Input, Int, Output) -> Bool,
  input_collective_outputs_validator: fn(Input, List<Output>) -> Bool,
  input_index: Int,
  output_indices: List<Int>,
  own_ref: OutputReference,
  inputs: List<Input>,
  outputs: List<Output>,
  double_satisfaction_prevented: Bool,
) -> Bool {
  expect double_satisfaction_prevented
  expect Some(input) = inputs |> list.at(input_index)

  let
    _,
    all_outputs,
  <-
    list_extra.foldr2(
      output_indices,
      // sufficiently large index to ensure first check passes
      511,
      [],
      fn(curr_ix, prev_ix, outputs_acc, return) {
        if curr_ix < prev_ix {
          expect Some(out_utxo) = outputs |> list.at(curr_ix)
          expect input_output_validator(input, curr_ix, out_utxo)
          return(curr_ix, list.push(outputs_acc, out_utxo))
        } else {
          fail @"Output indices must be in ascending order"
        }
      },
    )

  // Indicated input must match the spending one.
  expect (own_ref == input.output_reference)?

  input_collective_outputs_validator(input, all_outputs)
}

pub fn spend(
  withdraw_script_hash: ScriptHash,
  withdraw_redeemer_validator: fn(Redeemer, Lovelace) -> Bool,
  tx: Transaction,
) -> Bool {
  let Transaction { redeemers, withdrawals, .. } = tx

  let withdraw_script = Script(withdraw_script_hash)

  let withdraw_purpose = Withdraw(withdraw_script)

  let withdraw_redeemer: Redeemer =
    redeemers |> pairs.get_first(withdraw_purpose)

  expect Some(withdraw_amount) = withdrawals |> pairs.get_first(withdraw_script)

  withdraw_redeemer_validator(withdraw_redeemer, withdraw_amount)
}

pub fn spend_minimal(withdraw_script_hash: ScriptHash, tx: Transaction) -> Bool {
  let Transaction { withdrawals, .. } = tx

  let withdraw_script = Script(withdraw_script_hash)

  when withdrawals |> pairs.get_first(withdraw_script) is {
    Some(_) -> True
    _ -> False
  }
}

pub fn withdraw(
  withdrawal_logic: fn(a, ScriptHash, Transaction) -> Bool,
  redeemer: a,
  stake_cred: Credential,
  tx: Transaction,
) -> Bool {
  expect Script(own_validator) = stake_cred

  withdrawal_logic(redeemer, own_validator, tx)
}

pub fn spend(
  mint_script_hash: PolicyId,
  mint_redeemer_validator: fn(Redeemer) -> Bool,
  mint_tokens_validator: fn(Dict<AssetName, Int>) -> Bool,
  tx: Transaction,
) -> Bool {
  let Transaction { mint, redeemers, .. } = tx

  let mint_purpose = Mint(mint_script_hash)

  let mint_redeemer: Redeemer = redeemers |> pairs.get_first(mint_purpose)

  let tokens = mint |> assets.tokens(mint_script_hash)

  and {
    mint_redeemer_validator(mint_redeemer),
    mint_tokens_validator(tokens),
  }
}

pub fn spend_minimal(mint_script_hash: PolicyId, tx: Transaction) -> Bool {
  !(tx.mint |> assets.tokens(mint_script_hash) |> dict.is_empty)
}

pub fn sort_inputs(inputs: List<Input>) -> List<Input> {
  inputs
    |> list.sort(
        fn(i0: Input, i1: Input) {
          output_reference.compare(i0.output_reference, i1.output_reference)
        },
      )
}

pub fn resolve_output_reference(
  inputs: List<Input>,
  output_ref: OutputReference,
) -> Output {
  expect [input, ..] = inputs

  if input.output_reference == output_ref {
    input.output
  } else {
    resolve_output_reference(builtin.tail_list(inputs), output_ref)
  }
}

pub fn authentic_input_is_reproduced_unchanged(
  auth_symbol: PolicyId,
  optional_auth_name: Option<AssetName>,
  in_utxo: Output,
  out_utxo: Output,
) -> Bool {
  expect Output {
    value: in_val,
    datum: in_dat,
    address: in_addr,
    reference_script: None,
  } = in_utxo
  expect Output {
    value: out_val,
    datum: out_dat,
    address: out_addr,
    reference_script: None,
  } = out_utxo

  expect [_, (in_sym, in_name, in_quantity)] = in_val |> assets.flatten

  let name_is_authentic =
    when optional_auth_name is {
      Some(auth_name) -> in_name == auth_name
      None -> True
    }
  and {
    in_val == out_val,
    in_dat == out_dat,
    in_addr.payment_credential == out_addr.payment_credential,
    in_sym == auth_symbol,
    name_is_authentic,
    in_quantity == 1,
  }
}

pub fn sum_of_squares(xs: List<Int>) -> Int {
  xs |> foldl(0, fn(x, acc) { acc + x * x })
}

pub fn find_index_of_first_script_input(inputs: List<Input>) -> Int {
  expect Some(input_index) =
    list.indexed_foldr(
      inputs,
      None,
      fn(i, input, m_i) {
        when m_i is {
          Some(_) -> m_i
          None ->
            when input.output.address.payment_credential is {
              Script(_) -> Some(i)
              _ -> None
            }
        }
      },
    )
  input_index
}

pub fn single_specific_asset_value_fuzzer(policyId: ByteArray) -> Fuzzer<Value> {
  let token_name <- and_then(fuzz.bytearray_between(0, 32))
  let quantity <- and_then(fuzz.int_at_least(1))
  fuzz.constant(assets.from_asset(policyId, token_name, quantity))
}

pub fn single_asset_value_fuzzer() -> Fuzzer<Value> {
  let policyId <- and_then(fuzz.bytearray_fixed(28))
  single_specific_asset_value_fuzzer(policyId)
}

pub fn mint_fuzzer() -> Fuzzer<Value> {
  let other_assets_values <-
    and_then(fuzz.list_between(single_asset_value_fuzzer(), 1, 10))
  fuzz.constant(
    list.foldl(
      other_assets_values,
      assets.zero,
      fn(v, acc) { assets.merge(v, acc) },
    ),
  )
}

pub fn value_fuzzer(min_lovelaces: Int) -> Fuzzer<Value> {
  let lovelace_count <- and_then(fuzz.int_at_least(min_lovelaces))
  let lovelace_value = assets.from_lovelace(lovelace_count)
  let other_assets_values <-
    and_then(fuzz.list_at_most(single_asset_value_fuzzer(), 10))
  fuzz.constant(
    list.foldl(
      other_assets_values,
      lovelace_value,
      fn(v, acc) { assets.merge(v, acc) },
    ),
  )
}

pub fn output_reference_fuzzer() -> Fuzzer<OutputReference> {
  let ref_bytes <- and_then(fuzz.bytearray_fixed(32))
  let output_index <- and_then(fuzz.int_between(0, 512))
  fuzz.constant(OutputReference { transaction_id: ref_bytes, output_index })
}

pub fn user_output_fuzzer() -> Fuzzer<Output> {
  let pkh <- and_then(fuzz.bytearray_fixed(28))
  let skh <- and_then(fuzz.bytearray_fixed(28))
  let v <- and_then(value_fuzzer(2_000_000))
  fuzz.constant(
    Output {
      address: Address {
        payment_credential: VerificationKey(pkh),
        stake_credential: Some(Inline(VerificationKey(skh))),
      },
      value: v,
      datum: NoDatum,
      reference_script: None,
    },
  )
}

pub fn user_input_fuzzer() -> Fuzzer<Input> {
  let output_reference <- and_then(output_reference_fuzzer())
  let output <- and_then(user_output_fuzzer())
  fuzz.constant(Input { output_reference, output })
}

pub fn specific_script_output_fuzzer(
  script_hash: ScriptHash,
  datum: Datum,
) -> Fuzzer<Output> {
  let v <- and_then(value_fuzzer(2_000_000))
  fuzz.constant(
    Output {
      address: Address {
        payment_credential: Script(script_hash),
        stake_credential: None,
      },
      value: v,
      datum,
      reference_script: None,
    },
  )
}

pub fn script_output_fuzzer(datum: Datum) -> Fuzzer<(ByteArray, Output)> {
  let script_hash <- and_then(fuzz.bytearray_fixed(28))
  let utxo <- and_then(specific_script_output_fuzzer(script_hash, datum))
  fuzz.constant((script_hash, utxo))
}

pub fn authentic_script_output_fuzzer(
  datum: Datum,
) -> Fuzzer<(ByteArray, Output)> {
  let script_hash <- and_then(fuzz.bytearray_fixed(28))
  let tn <- and_then(fuzz.bytearray_between(0, 32))
  let lovelace_value = assets.from_lovelace(2_000_000)
  let beacon = assets.from_asset(script_hash, tn, 1)
  let v = assets.merge(lovelace_value, beacon)
  fuzz.constant(
    (
      script_hash,
      Output {
        address: Address {
          payment_credential: Script(script_hash),
          stake_credential: None,
        },
        value: v,
        datum,
        reference_script: None,
      },
    ),
  )
}

pub fn specific_script_input_fuzzer(
  script_hash: ScriptHash,
  datum: Datum,
) -> Fuzzer<Input> {
  let output_reference <- and_then(output_reference_fuzzer())
  let output <- and_then(specific_script_output_fuzzer(script_hash, datum))
  fuzz.constant(Input { output_reference, output })
}

pub fn script_input_fuzzer(datum: Datum) -> Fuzzer<(ByteArray, Input)> {
  let output_reference <- and_then(output_reference_fuzzer())
  let (script_hash, output) <- and_then(script_output_fuzzer(datum))
  fuzz.constant((script_hash, Input { output_reference, output }))
}

pub fn authentic_script_input_fuzzer(datum: Datum) -> Fuzzer<(ByteArray, Input)> {
  let output_reference <- and_then(output_reference_fuzzer())
  let (script_hash, output) <- and_then(authentic_script_output_fuzzer(datum))
  fuzz.constant((script_hash, Input { output_reference, output }))
}

pub fn user_outputs_fuzzer() -> Fuzzer<List<Output>> {
  fuzz.list_between(user_output_fuzzer(), 1, 10)
}

pub fn user_inputs_fuzzer() -> Fuzzer<List<Input>> {
  fuzz.list_between(user_input_fuzzer(), 1, 10)
}

pub fn inputs_with_an_authentic_script_input_fuzzer(
  datum: Datum,
) -> Fuzzer<(ByteArray, Input, List<Input>)> {
  let wallet_inputs <- and_then(user_inputs_fuzzer())
  let (spend_script_hash, script_input) <-
    and_then(authentic_script_input_fuzzer(datum))
  fuzz.constant(
    (
      spend_script_hash,
      script_input,
      [script_input, ..wallet_inputs] |> sort_inputs,
    ),
  )
}

pub fn normalize_time_range(
  validity_range: ValidityRange,
) -> NormalizedTimeRange {
  let Interval {
    lower_bound: IntervalBound {
      bound_type: lower_bound_type,
      is_inclusive: lower_is_inclusive,
    },
    upper_bound: IntervalBound {
      bound_type: upper_bound_type,
      is_inclusive: upper_is_inclusive,
    },
  } = validity_range
  when (lower_bound_type, upper_bound_type) is {
    (interval.NegativeInfinity, interval.PositiveInfinity) -> Always
    (interval.NegativeInfinity, interval.Finite(upper_int)) ->
      FromNegInf { upper: resolve_upper(upper_int, upper_is_inclusive) }
    (interval.Finite(init_lower_int), interval.Finite(init_upper_int)) ->
      ClosedRange {
        lower: resolve_lower(init_lower_int, lower_is_inclusive),
        upper: resolve_upper(init_upper_int, upper_is_inclusive),
      }
    (interval.Finite(init_lower_int), interval.PositiveInfinity) ->
      ToPosInf { lower: resolve_lower(init_lower_int, lower_is_inclusive) }
    _ -> fail @"Improper validity range encountered"
  }
}

