fn grab_a_policy_from_mint(m: Value) -> Fuzzer<(PolicyId, Value)> {
  let policies = m |> assets.policies
  fuzz.map(
    fuzz.int_between(0, list.length(policies) - 1),
    fn(i) {
      trace list.length(policies)
      trace i
      expect Some(p) = list.at(policies, i)
      (p, m)
    },
  )
}

fn resolve_lower(init_lower: Int, is_inclusive: Bool) -> Int {
  if is_inclusive {
    init_lower
  } else {
    init_lower + 1
  }
}

fn resolve_upper(init_upper: Int, is_inclusive: Bool) -> Int {
  if is_inclusive {
    init_upper
  } else {
    init_upper - 1
  }
}
